"""**Inheritance**"""

#parent class
class Animal():
  def __init__(self):
    print("animal is created")
  def toString(self):
    print("animal")
  def walk(self):
    print("animal walk")

#child class
class Monkey(Animal):
  def __init__(self):
    super().__init__() #parent classın init'ini kullanabiliyoz
    print("monkey is created")
  def toString(self):
    return("monkey")
  def climb(self):
    return("monkey can climb")
#child class
class Bird(Animal):
  def __init__(self):
    super().__init__()
    print("bird is created")
  def fly(self):
    return "fly"

m1=Monkey()
print(m1.toString())

m1.walk()

m1.climb()

b1=Bird()

b1.walk()

b1.fly()

