#parent class
class Website():
  def __init__(self,name,surname):
    self.name=name
    self.surname=surname
  def loginInfo(self):
    return(self.name+" "+self.surname+" giriş yaptı")
#child class
class Website1(Website):
  def __init__(self, name, surname,id):
    Website.__init__(self,name,surname)#website classına git name ve surname i öğren
    self.id=id
  def login(self):
    return(self.name+" "+self.surname+" "+self.id+" giriş yaptı")

class Website2(Website):
  def __init__(self, name, surname,email):
    Website.__init__(self,name, surname)
    self.email=email
  def login(self):
    return(self.name+" "+self.surname+" "+self.email+" giriş yaptı")

p1=Website("özgün","can")

p1.loginInfo()

