from abc import ABC,abstractmethod
#inheritance
class Shape(ABC):
  """
  Shape=super class/abstrac class
  """
  #abstract method
  @abstractmethod
  def area(self):
    pass
  @abstractmethod
  def perimeter(self):
    pass
  #overriding and polymorphism
  def toString(self):
    pass

class Square(Shape): #subclass
  def __init__(self,edge):
    self.__edge=edge
  def area(self):
    result=self.__edge**2
    return result
  def perimeter(self):
    return self.__edge*4
#ovveride and ploymorphism
  def toString(self):
    print("Square edge",self.__edge)

class Circle(Shape):
  pi=3.14#constant variable
  def __init__(self,radius):
    self.__radius=radius
  def area(self):
    return self.pi*self.__radius**2
  def perimeter(self):
    return self.__radius*2*self.pi
  def toString(self):
    print("Circle radius:",self.__radius)

c=Circle(5)

c.area()

c.perimeter()

c.toString()

s=Square(5)

s.area()

s.perimeter()

